<template>
  <div class="form-sign">
    <s-form
      v-model="fields"
      action="/path/to/api"
      @pre-submit="preSubmit"
      @submit="submit"
    >
      <v-text-field
        ref="v-text-name"
        v-model="fields.name"
        :rules="rules.name"
        label="Your name"
      />
      <v-text-field
        v-model="fields.emailAddress"
        :rules="rules.emailAddress"
        label="E-mailadres"
        prepend-icon="mdi-emailAddress"
      />
      <v-text-field
        v-model="fields.location"
        :rules="rules.location"
        label="Location">
        <template slot="append"><a :href="'http://www.google.com/maps/place/' + fields.location" target="_blank" tabindex="-1">link</a></template>
      </v-text-field>
      <v-text-field
        v-model="fields.reason"
        :rules="rules.reason"
        label="Reason"
      />

      <v-divider class="my-4"/>
      <v-file-input
        v-model="fields.files"
        :rules="rules.files"
        label="Files"
        multiple
        counter
        :truncate-length="$vuetify.breakpoint.smAndUp ? 22 : 16"
      >
        <template v-slot:selection="{ index, text }">
          <v-chip
            close
            @click:close="fields.files.splice(index, 1)"
          >
            {{ text }}
          </v-chip>
        </template>
      </v-file-input>
      <template v-if="$vuetify.breakpoint.smAndUp">
        <v-divider class="my-4"/>
        <s-drop-files v-model="fields.files"/>
      </template>
      <v-divider class="my-4"/>
      <template v-slot:action="{ disabled, submitting }">
        <v-btn
          :disabled="disabled"
          :loading="submitting"
          :block="$vuetify.breakpoint.xsOnly"
          type="submit"
          color="primary"
          class="mt-4"
          large
          rounded
        >
          Upload files
          <v-icon
            right
            size="24"
          >
            mdi-cloud-upload
          </v-icon>
        </v-btn>
      </template>
    </s-form>
    <v-expand-transition>
      <div v-if="verified.length || unverified.length">
        <div class="title primary--text mt-4">
          Status
        </div>
        <v-divider class="mt-1 mb-2"/>
        <template v-if="verified.length">
          <div class="subtitle-1 mb-2">
            Bestanden ontvangen
          </div>
          <s-status
            :items="verified"
            :truncate-length="$vuetify.breakpoint.xsOnly ? 22 : Infinity"
          />
        </template>
        <template v-if="unverified.length">
          <v-divider
            v-if="verified.length"
            class="my-3"
          />
          <div class="subtitle-1 mb-2">
            Rejected files
          </div>
          <s-status
            :items="unverified"
            :truncate-length="$vuetify.breakpoint.xsOnly ? 22 : Infinity"
          />
        </template>
      </div>
    </v-expand-transition>
  </div>
</template>

<script>
import SForm from '@/components/SForm';
import SDropFiles from '@/components/SDropFiles';
import SStatus from '@/components/SStatus';
import {base64StringToBlob} from "blob-util";

const base64TestFile = "";


export default {
  name: 'FormSign',

  components: {SStatus, SDropFiles, SForm},

  data: () => ({
    fields: process.env.NODE_ENV === 'development' ? {
      name: 'Jane Doe',
      emailAddress: `janedoe-${Date.now()}@sphereon.com`,
      location: 'Maarssen NL',
      reason: 'Testing...',
      files: [new File([base64StringToBlob(base64TestFile, "application/pdf")], `TEST PDF FILE.pdf`)]
    } : {
      name: '',
      emailAddress: '',
      location: '',
      reason: '',
      files: []
    },

    rules: {
      name: [
        v => !!v || 'Enter your full name',
      ],

      emailAddress: [
        v => !!v || 'Enter your e-mail address',
        v => /.+@.+\..+/.test(v) || 'This is not a valid emailAddress address'
      ],

      location: [
        v => !!v || 'Enter your location, either a name or longitude-latitude',
      ],

      reason: [
        v => !!v || 'Enter a reason',
      ],

      files: [
        v => !!v.length || 'Selecteer minimaal één bestand',
        v => v.length <= 5 || 'Maximaal vijf bestanden toegestaan'
      ]
    },

    verified: [],
    unverified: []
  }),

  mounted() {
    this.getLocation();
    this.$refs["v-text-name"].focus();
  },

  methods: {
    getLocation() {
      navigator.geolocation.getCurrentPosition(
        position => {
          this.fields.location = position.coords.latitude + "," + position.coords.longitude
        },
        error => {
          console.log(error.message);
        });
      return undefined;
    },

    preSubmit() {
      this.verified = [];
      this.unverified = [];
    },

    submit(response) {
      // TODO: handle response
      console.log(response);

      this.verified = [...this.fields.files.slice(1)];
      this.unverified = [...this.fields.files.slice(0, 1)];
    }
  }
}
</script>
